import Vue from 'vue';
import Router from 'vue-router';
import homeRoutes from './modules/home';
import aboutRoutes from './modules/about';

Vue.use(Router);

const routes = [
  ...homeRoutes,
  ...aboutRoutes
];

export default new Router({
  mode: 'history',
  routes
});
